$(".btn-row-active").on("click",".btn-active-admin",function(){var a=$(this).data("controller");var b=HOSTNAME+"admin/"+a+"/active";var c=$(this).data("id");switch(a){case"opening":message="Kích hoạt lịch khai giảng này?";break;case"banner":message="Kích hoạt banner này?";break}if(confirm(message)){$.ajax({method:"get",url:b,data:{id:c},success:function(d){if(d.status==200){$(".row-active-"+c).html('<button class="btn btn-success btn-sm btn-deactive-admin" type="button" data-id="'+c+'" data-controller="'+a+'">Đang Kích Hoạt</button>')}},error:function(e,d){console.log(errorHandle(e,d))}})}});$(".btn-row-active").on("click",".btn-deactive-admin",function(){var a=$(this).data("controller");var b=HOSTNAME+"admin/"+a+"/deactive";var c=$(this).data("id");switch(a){case"opening":message="Tắt lịch khai giảng này?";break;case"banner":message="Tắt banner này?";break}if(confirm(message)){$.ajax({method:"get",url:b,data:{id:c},success:function(d){if(d.status==200){$(".row-active-"+c).html('<button class="btn btn-warning btn-sm btn-active-admin" type="button" data-id="'+c+'" data-controller="'+a+'">Không Kích Hoạt</button>')}},error:function(e,d){console.log(errorHandle(e,d))}})}});