$(document).ready(function(){$(".close-classic").click(function(){var a=$(this).data("controller");var b=HOSTNAME+"admin/"+a+"/remove_image";var c=$(this).data("key");var e=$(this).data("id");var d=$(this).data("image");if(confirm("Chắc chắn xóa?")){$.ajax({method:"get",url:b,data:{id:e,image:d},success:function(f){console.log(f);if(f.status==200&&f.message==""){$(".remove_"+c).fadeOut()}if(f.status==200&&f.message!=""){alert(f.message)}},error:function(g,f){console.log(errorHandle(g,f))}})}});$(".btn-active-img").click(function(){var a=$(this).data("controller");var b=HOSTNAME+"admin/"+a+"/active_image";var d=$(this).data("id");var c=$(this).data("image");if(confirm("Chắc chắn chọn ảnh này làm Avatar?")){$.ajax({method:"get",url:b,data:{id:d,image:c},success:function(e){if(e.status==200){location.reload()}},error:function(f,e){console.log(errorHandle(f,e))}})}})});